import react from "react";
import CartCard from "../Components/CartCard";
import PriceCalculator from "../Components/PriceCalculator";
import { useApp } from "../context/App-context";

export default function Cart() {
  const { state, dispatch } = useApp();
  const cartItems=state.cartItems
  return (
    <div className="cart-body">
    {cartItems.length==0? <div className="empty-cart"> 
    <script src="https://unpkg.com/@lottiefiles/lottie-player@latest/dist/lottie-player.js"></script>
    <lottie-player src="https://assets9.lottiefiles.com/packages/lf20_tma2oC.json"  background="transparent"  speed="1"  style="width: 300px; height: 300px;"  loop controls autoplay></lottie-player>
    <h1>Please add Some products in your cart</h1></div>:
    <div>
        <h1>Cart Summary</h1>
      <div className="cart-summary-wrap">
        <div className="cart-card-wrap">
            {cartItems.map(item=><CartCard item={item}/>)}
        </div>
       <PriceCalculator cartItems={cartItems}/>
      </div>
    </div>
    }
      
    </div>
  );
}
