import react, { useEffect, useState } from "react";
import CartCard from "../Components/CartCard";
import PriceCalculator from "../Components/PriceCalculator";
import { useApp } from "../context/App-context";
import toast, { Toaster } from "react-hot-toast";
import axios from "axios";
export default function Cart() {
  const { state, dispatch } = useApp();
  // const userName = state.userName;
  const userName="cartTester2"
  const [cart, setCart] = useState([]);
  async function cartLoadHandler() {
    await axios({
      method: "get",
      url: `http://localhost:3000/cart/${userName}`,
      data: {}
    })
      .then(function (response) {
        console.log(response.data);
        toast.success("cart loaded succesfully");
        const { cart } = response.data;
        console.log(cart.itemsInCart);
        setCart(() => cart.itemsInCart);
      })
      .catch(function (error) {
        console.log(error.response);
        toast.error("Invalid Username or Password");
      });
  }
  useEffect(cartLoadHandler, []);
  const cartItems = cart;

  return (
    <div className="cart-body">
      {userName == null ? (
        <div className="empty-cart">
          <h1>Please Login or Sign up first</h1>
        </div>
      ) : cartItems.length == 0 ? (
        <div className="empty-cart">
          <h1>Please add Some products in your cart</h1>
        </div>
      ) : (
        <div>
          <h1 className="text">Cart Summary </h1>
          <div className="cart-summary-wrap">
            <div className="cart-card-wrap">
              {cartItems.map((item) => (
                <CartCard item={item} />
              ))}
            </div>
            <PriceCalculator cartItems={cartItems} />
          </div>
        </div>
      )}
    </div>
  );
}
