  const cartItems=[{
    name: "Nvidia RTX 3070",
    img: "https://ik.imagekit.io/harshit/RTX_3070__KPu_TvMZ.png",
    description: "lorem ipusum",
    highlights:"8GB Memory ",
    price: 45000,
    shipping: 500,
    inStock: true,
    type: "graphics card",
    quantity:1
}, {
    name: "Nzxt H510 ",
