    price: 36000,
    shipping: 500,
    inStock: true,
    type: "case",
    quantity:1
}, {
    name: "Lian li O11 ",
    img: "https://ik.imagekit.io/harshit/Lian_li_o11_q34_HmCnD.png",
    description: "lorem ipusum",
    highlights:"lorem imspum",
    price: 15000,
    inStock: true,
    shipping: 500,
    type: "case",
    quantity:1
}]
  console.log(cartItems)
  return (
    <div className="cart-body">
      <h1>Cart Summary</h1>
      <div className="cart-summary-wrap">
        <div className="cart-card-wrap">
            {cartItems.map(item=><CartCard item={item}/>)}
          
        </div>
        <div className="price-card-wrap">card</div>
      </div>
    </div>
  );
}
