import react, { useState } from "react";

export default function PriceCalculator({ cartItems }) {
console.log(cartItems)
const totalPriceArr=[]
cartItems.map(item=>totalPriceArr.push(item.price))
const cartTotal=totalPriceArr.reduce((acc,crr)=>acc+crr)
const isShippingFree= cartItems.map(item=>item.shipping==0 ? fa : false )

return (
    <div className="price-calculator">
      <h1>Price Summary</h1>
      <h2>Cart Subtotal <span>{cartTotal}</span></h2>
      <h2>Shipping Charges <span>{isShippingFree?"Free":500}</span></h2>
    </div>
  );
}
