import react, { useReducer, useState } from "react";

import ProductCard from "../Components/Product-Card"
// import ProductFilter from "../Components/ProductFilter"
import {
    AllProducts
} from "./primaryDB"
// -----------------------------------------------------------------------------------

function ProductFilter() {
  
  const [inputPrice, setInputPice] = useState(150);
    const getSordtedData =(productList,sortby)=>{
        if(sortby && sortby=="LOWtoHIGH"){
        return (productList.sort((a,b)=>a.price -b.price))
        }

        if(sortby && sortby=="HIGHtoLOW"){
            return (productList.sort((a,b)=>b.price -a.price))
        }
  }

  const getFilteredData =(productList,sortby)=>{
    return productList.filter(item=>item.type == "case")
}
  console.log(AllProducts) 
  getFilteredData(AllProducts)
  const testprod = [{type:"case",title:"theralcare"}
  , {type:"case",title:"not-theralcare"}, {type:"gpu",title:"NVIDIA"},{type:"cpu",title:"AMD"}];

  const result = testprod.filter(item => item.type =='cpu');
  // const resultTwo = words.find(word => word.length =='elite');
  
  console.log(result);
  // console.log(resultTwo);


  const reducer= (state,action)=>{
    switch(action.type){
        case "SORT":
        return {...state,sortby:action.payload};
    }
  }
  
    const [state,dispatch]=useReducer(reducer,{sortby:null})
    // getSordtedData(AllProducts,state.sortby)

    return (
      <div className="filter-wrap">
        {/* ----------Sorting ----------- */}
        <div className="sorting-products">
          <h1>
            Sort by 
          </h1>
          <label><input type="radio" name="sort" onChange={()=>dispatch({type:"SORT",payload:"LOWtoHIGH"})}/> <span className="sorting-text">Low to High</span>
          </label>
          <label> <input type="radio" name="sort" onChange={()=>dispatch({type:"SORT",payload:"HIGHtoLOW"})}/> <span className="sorting-text">High To Low</span>
          </label>
        </div>
  
        {/* ----------Price Filter----------- */}
        <div className="price-filter">
        {console.log(inputPrice)}
        <h1>Price Range</h1>
          <input
            className="price"
            type="range"
            min="2"
            max="150"
            step="4"
            onChange={(e) => setInputPice(() => e.target.value)}
          />
        </div>
        
        {/* ---------category--------- */}
  
        <div className="categories-filter">
        <h1>
            Categories
        </h1>
          <label for="cabinet">
            <input type="radio" id="cabinet" name="categories" value="huey" />{" "}
            <span className="category-text">Cabinets</span>{" "}
          </label>
  
          <label>
            {" "}
            <input type="radio" id="huey" name="categories" value="huey" />{" "}
            <span className="category-text">Graphics Card</span>{" "}
          </label>
  
          <label>
            <input type="radio" id="huey" name="categories" value="huey" />
            <span className="category-text">Proccessor</span>{" "}
          </label>
  
          <label>
            {" "}
            <input type="radio" id="huey" name="categories" value="huey" />{" "}
            <span className="category-text">Storage</span>
          </label>
  
          <label>
            {" "}
            <input type="radio" id="huey" name="categories" value="huey" />{" "}
            <span className="category-text"> AIO</span>
          </label>
        </div>
      </div>
    );
  }
  
export default function Products() {
    return (
        <div className="products-page">
        <ProductFilter />
        <div className="product-wrap">
        <div className="list-tag">
            { AllProducts.map((item) => {
                console.log("hello")
                return (
                    <div>
                    <ProductCard title={item.name} src={item.img} price={item.price} highlights={item.highlights}/> 
                    </div>
                )
            })}
            </div></div>
        </div>
        

    )


}
