import react from "react";
import { useApp } from "../context/App-context";

export default function CartCard({ item }) {
  function QuantityHandler() {
    return (
      <div>
        <button>-</button>
        <span>
          <h6> {item.quantity}</h6>
        </span>
        <button>+</button>
      </div>
    );
  }
const {dispatch}=useApp()
  return (
    <div className="cart-card">
      <div className="cart-card-img">
        <img src={item.img} />
      </div>
      <div className="cart-card-content">
        <h1>{item.name}</h1>
        <div>
          <h2>
            {" "}
            Shipping Charges <span className="spaced">₹ {item.shipping}</span>
          </h2>
          <h2> Esitmated Delivery{item.delivery}</h2>
          <h2> Quantity 5</h2>
        </div>
      </div>
      <div className="price-remove">
        <h2>
          Price <span className="spaced">₹ {item.price}</span>
        </h2>
        <h2 className="Remove" onClick={()=>dis}>
          Remove{" "}   
        </h2>
      </div>
    </div>
  );
}
